import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import Row from '../Monitor/MonitorRow';
import '../Session/SessionHistory.css'
import Detail from './AppointmentDetail';
import Edit from './AppointmentEdit';
import Record from './AppointmentRecord';
import Create from './AppointmentCreate';

function AppointmentPopUpModal({ isOpen, onClose, date, time, counsellor, title, isCreateOpen }) {

    const [detailOpen, setDetailOpen] = useState(isCreateOpen)
    const [editOpen, setEditOpen] = useState(false)
    const [recordOpen, setRecordOpen] = useState(false)

    const detail = {
        date: date,
        time: time,
        counsellor: counsellor,
        title: title
    }

    const MODAL_STYLES = {
        position: 'fixed',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        backgroundColor: '#FFF',
        // padding: '2rem',
        height: '87%',
        zIndex: 1000,
        width: '90%',
        margin: '1rem',
        overflowY: 'auto'
    }

    const OVERLAY_STYLES = {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        backgroundColor: 'rgba(0, 0, 0, .7)',
        zIndex: 1000,
    }

    if (!isOpen) {
        document.getElementById('mainBody').style.overflow = 'auto'
        return null
    }
    else {
        document.getElementById('mainBody').style.overflow = 'hidden'
        return ReactDOM.createPortal(
            <>
                <div style={OVERLAY_STYLES}>
                    <div style={MODAL_STYLES}>
                        <div style={{ padding: '1rem' }}>
                            <Row
                                isOpen={!isCreateOpen}
                                profilePic='/images/jingyuan.webp'
                                name='Johnny'
                                studentId='1800795'
                                course='Software Engineering'
                                trimester='Y5S2'
                                attendance='83'
                                mentalHealth='63'
                                address='/profile'
                                appt='01/02/2023'
                                lastTested='02/03/2022'
                            ></Row>
                            <Detail
                                isOpen={detailOpen}
                                onEdit={() => { setDetailOpen(false); setEditOpen(true) }}
                                onRecord={() => { setDetailOpen(false); setRecordOpen(true) }}
                                onClose={onClose}
                                detail={detail}
                            />
                            <Edit isOpen={editOpen} onClose={() => { setEditOpen(false); setDetailOpen(true) }}
                                detail={detail}
                            />
                            <Record isOpen={recordOpen} onClose={() => { setRecordOpen(false); setDetailOpen(true) }}
                                detail={detail}
                            />
                            <Create isOpen={isCreateOpen} onClose={()=>{isOpen(false)}}></Create>
                        </div>
                    </div>
                </div>
            </>,
            document.getElementById('AppointmentPopUpModal')
        )
    }

}

export default AppointmentPopUpModal
