import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import '../../../App';
import { Button } from '../Button';
import './Home.css';
import Monitor from '../Monitor/MonitorList';
import Title from '../Title';

function Home() {

  // const location = useLocation();
  // const { role } = location.state;
  // // const [role, setRole] = useState('counsellor')
  // console.log(role)

  
  const [isCounsellor, setIsCounsellor] = useState("counsellor")
  const [isStudent, setIsStudent] = useState("student")
  const [isAcademicAdvisor, setIsAcademicAdvisor] = useState("advisor")
  const [role, setRole] = useState(isCounsellor)

  if (role == 'counsellor') {
    return (
      <div className='hero-container'>
        <Title title="Counsellor Home" />
        <Monitor />
      </div>
    );
  }
  else if (role == 'student') {
    return (
      <div className='hero-container'>
        <Title title="Student Home" />
        <Monitor />
      </div>
    );
  }
  else {
    return (
      <div className='hero-container'>
        <Title title="Academic Advisor Home" />
        <Monitor />
      </div>
    );
  }
}

export default Home;
